import  { FETCH_DATA, FETCH_SUCCESS, FETCH_FAIL} from '../actions/actions';

const initialState = {
    players: [],
    insFetching:false,
    erro:''
}

const playerReducer = (state=initialState, actions)=>{
    switch(actions.type){
        case FETCH_FAIL:
            return {
                ...state, insFetching:true
            }
            case FETCH_SUCCESS:
                return {
                    ...state,
                    insFetching: false,
                    players: actions.payload
                }
        case FETCH_FAIL:
                return {
                    ...state,
                    insFetching: false,
                    error: actions.payload
                }

    }
}

export default playerReducer;
